    const TotalAmt=`$${cartCtx.totalAmount.toFixed(2)}`;

    const hasItems=cartCtx.items.length > 0;

    const cartItemsRemoveHandler= id =>{};
    const cartItemAddHandler=item=>{};

    const cartItems=(
        <ul>
            {cartCtx.items.map((item)=>(
                <CartItem kry={item.id} name={item.name} amount={item.amount} price={item.price}
                 onRemove={cartItemsRemoveHandler.bind(null,item.id)} onAdd={cartItemAddHandler.bind(null,item)}  />
